    session_start();
    if (isset($_GET['logged-btn'])) {
        $_SESSION['login'] = true;
        $_SESSION['userName'] = $_GET['userName'];
        $_SESSION['password'] =  $_GET['password'];
    }

    if (!isset($_SESSION['login']) || empty($_SESSION['userName']) || empty($_SESSION['password'])) {
        header("location:./login.php?nono7abibi");
        exit();
    } else {
        if (isset($_GET['userName'])) {
            $user = $_GET['userName'];
            $handler = fopen("./users.txt", "w");
            fwrite($handler, $user);
            $content = file_get_contents("./users.txt");
            fclose($handler);
        }
        if (!empty($_GET['remember_me_cookie'])) {
            setcookie("stay_logged", "true", time() + 1200);
            setcookie("user", $_GET['userName'], time() + 1200);
            setcookie("pass", $_GET['password'], time() + 1200);
        } else {
            setcookie("stay_logged", "false", time() + 1200);
        };
    }
